					rotateAnimator.addUpdateListener(new android.animation.ValueAnimator.AnimatorUpdateListener() {
							@Override public void onAnimationUpdate(android.animation.ValueAnimator animation) {
								rotate = (float)animation.getAnimatedValue();
								if (invalidateListener != null) {
									invalidateListener.reDraw();
								}
							}
						});

					android.animation.ValueAnimator scaleAnimator = android.animation.ValueAnimator.ofFloat(1f, 0.8f, 1f);
					scaleAnimator.setDuration(1000);
					scaleAnimator.setRepeatCount(android.animation.ValueAnimator.INFINITE);
					scaleAnimator.addUpdateListener(new android.animation.ValueAnimator.AnimatorUpdateListener() {
							@Override public void onAnimationUpdate(android.animation.ValueAnimator animation) {
								scale = (float)animation.getAnimatedValue();
								if (invalidateListener != null) {
									invalidateListener.reDraw();
								}
							}
						});

					android.animation.AnimatorSet animatorSet = new android.animation.AnimatorSet();
					animatorSet.play(rotateAnimator).with(scaleAnimator);
					animatorSet.start();
				}

				@Override public void draw(android.graphics.Canvas canvas) {
					canvas.save();
					canvas.scale(scale, scale, center.x, center.y);
					canvas.rotate(rotate, center.x, center.y);
					eye.draw(canvas);
					eyeBound.draw(canvas);
					for (int i = 0; i < numberOfSharingan; i++) {
						canvas.save();
						canvas.rotate(i * 120, center.x, center.y);
						sharingans[i].draw(canvas);
						canvas.restore();
					}
					canvas.restore();
				}
