/**
 * Base class that contains common implementation for all
 * visualizers.
 * Created by gautam chibde on 28/10/17.
 */

abstract public static class BaseVisualizer extends View {
    protected byte[] bytes;
    protected Paint paint;
    protected android.media.audiofx.Visualizer visualizer;
    protected int color = Color.BLUE;

    public BaseVisualizer(Context context) {
        super(context);
        init(null);
        init();
    }

    public BaseVisualizer(Context context,AttributeSet attrs) {
        super(context, attrs);
        init(attrs);
        init();
    }

    public BaseVisualizer(Context context,AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        init(attrs);
        init();
    }

    private void init(AttributeSet attributeSet) {
        paint = new Paint();
    }

    /**
     * Set color to visualizer with color resource id.
     *
     * @param color color resource id.
     */
    public void setColor(int color) {
        this.color = color;
        this.paint.setColor(this.color);
    }

    /**
     * @deprecated will be removed in next version use {@link BaseVisualizer#setPlayer(int)} instead
     * @param mediaPlayer MediaPlayer
     */
    @Deprecated
    public void setPlayer(MediaPlayer mediaPlayer) {
        setPlayer(mediaPlayer.getAudioSessionId());
    }

    public void setPlayer(int audioSessionId) {
        visualizer = new android.media.audiofx.Visualizer(audioSessionId);
        visualizer.setEnabled(false);
        visualizer.setCaptureSize(android.media.audiofx.Visualizer.getCaptureSizeRange()[1]);

        visualizer.setDataCaptureListener(new android.media.audiofx.Visualizer.OnDataCaptureListener() {
            @Override
            public void onWaveFormDataCapture(android.media.audiofx.Visualizer visualizer, byte[] bytes,
                                              int samplingRate) {
                BaseVisualizer.this.bytes = bytes;
                invalidate();
            }

            @Override
            public void onFftDataCapture(android.media.audiofx.Visualizer visualizer, byte[] bytes,
                                         int samplingRate) {
            }
        }, android.media.audiofx.Visualizer.getMaxCaptureRate() / 2, true, false);

        visualizer.setEnabled(true);
    }

    public void release() {
        //will be null if setPlayer hasn't yet been called
        if (visualizer == null)
            return;

        visualizer.release();
        bytes = null;
        invalidate();
    }

    public android.media.audiofx.Visualizer getVisualizer() {
        return visualizer;
    }

    protected abstract void init();
}
